import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import './chat-step-1.css';

const ChatStep1 = (props) => {
  const [chatProcess, setChatProcess] = useState(1);
  const [videoSrc, setVideoSrc] = useState('');
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const loadingGif = '/Infinite-Loader.gif'; // Ensure this path is correct

  const handleChange = (event) => {
    setInput(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setVideoSrc('');
    setChatProcess(2);
    setIsLoading(true); // Start loading
    try {
      const response = await fetch('http://localhost:5001/api/getWord', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ input })
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      setVideoSrc(url);
      console.log('Video source:', url);
    } catch (error) {
      console.error('There was a problem with the fetch operation:', error);
    } finally {
      setIsLoading(false); // Stop loading
    }
  };

  return (
    <div className={`chat-step-1-container ${props.rootClassName}`}>
      <h1 className="chat-step-1-header">
        {chatProcess === 1 ? (
          <span className="chat-step-1-text08 ai-word">DESCRIBE</span>
        ) : (
          <span className="chat-step-1-text08 ai-word">
            {isLoading ? 'Generating' : 'Download'}
          </span>
        )}
      </h1>
      <h1 className="chat-step-1-header1">
        {chatProcess === 1 ? (
          <span className="chat-step-1-text06">Video in DETAIL</span>
        ) : (
          <span className="chat-step-1-text06">
            {isLoading ? 'Come back in a few' : '& Publish video'}
          </span>
        )}
      </h1>
      <div className="chat-step-1-container1">
        <div className="chat-step-1-container2">
          {chatProcess === 1 && (
            <textarea
              placeholder="Describe your ideal video: Include details like the type of video (e.g., motivational, tutorial), preferred length, platform (e.g., TikTok, YouTube), background music, voiceover style, and any specific text or captions you want. For example: 'I want a 40-second motivational video for TikTok with calming music, a deep male voiceover, and yellow captions.'"
              className="chat-step-1-textarea textarea"
              value={input}
              onChange={handleChange}
            />
          )}
        </div>
        {chatProcess === 2 && isLoading && (
          <div className="chat-step-1-loading">
            <img src={loadingGif} alt="Loading..." className="loading-gif" />
          </div>
        )}
        {chatProcess === 2 && !isLoading && (
          <video
            src={videoSrc}
            muted = {false}
            poster="https://play.teleporthq.io/static/svg/videoposter.svg"
            autoPlay = {true}
            className="chat-step-1-video"
          />
        )}
      </div>
      {chatProcess === 1 && (
        <button
          type="submit"
          onClick={handleSubmit}
          className="chat-step-1-button ai-button"
        >
          <span className="chat-step-1-text">
            {props.text ?? <span>Create Video</span>}
          </span>
        </button>
      )}
      {chatProcess === 2 && !isLoading && (
        <button type="button" className="chat-step-1-button1 ai-button">
          <span className="chat-step-1-text01">
            {props.text2 ?? <span>TRY KIIBZZ AI</span>}
          </span>
          <br />
          <span className="chat-step-1-text04">
            {props.text3 ?? <span>Download</span>}
          </span>
        </button>
      )}
    </div>
  );
};

ChatStep1.defaultProps = {
  text3: undefined,
  header1111: undefined,
  text: undefined,
  rootClassName: '',
  videoSrc: '',
  header31: undefined,
  text2: undefined,
};

ChatStep1.propTypes = {
  text3: PropTypes.element,
  header1111: PropTypes.element,
  text: PropTypes.element,
  rootClassName: PropTypes.string,
  videoSrc: PropTypes.string,
  header31: PropTypes.element,
  text2: PropTypes.element,
};

export default ChatStep1;
